import Contact from "../Todo";
import { useSelector, useDispatch } from "react-redux";
import { contactsOperations, contactsSelectors } from "../../redux/contacts";
import styles from "./TodoList.module.css";

export default function TodoList() {
  const dispatch = useDispatch();
  const contacts = useSelector(contactsSelectors.getVisibleContacts);
  console.log(contacts);
  const onDeleteContact = (id) =>
    dispatch(contactsOperations.deleteContact(id));
  const onToggleCompleted = (id) =>
    dispatch(contactsOperations.toggleCompleted(id));
  return (
    <ul className={styles.list}>
      {contacts.map(({ id, name, number, completed }) => (
        <li key={id} className={completed ? styles.completed : styles.item}>
          <Contact
            name={name}
            number={number}
            completed={completed}
            onToggleCompleted={() =>
              onToggleCompleted({ id, completed: !completed })
            }
            onDelete={() => onDeleteContact(id)}
          />
        </li>
      ))}
    </ul>
  );
}
