import { useSelector } from "react-redux";
import styles from "./Stats.module.css";
import { contactsSelectors } from "../../redux/contacts";

export default function Stats() {
  const total = useSelector(contactsSelectors.getTotalContactsCount);
  const getAllContacts = (state) => state.contacts.items;
  const favorite = createSelector([getAllContacts], (contacts) => {
    return contacts.reduce(
      (total, todo) => (todo.completed ? total + 1 : total),
      0
    );
  });

  return (
    <div className={styles.container}>
      <p className={styles.item}>
        <span className={styles.value}>{total}</span>
        <span className={styles.label}>Всего</span>
      </p>
      <p className={styles.item}>
        <span className={styles.value}>{favorite}</span>
        <span className={styles.label}>Выполнено</span>
      </p>
    </div>
  );
}
